mapper.select:
         select
         distinct REPORT_DATE as REPORT_DATE
         from doufu_today_work t

		1=1
		 and REPORTER_NAME = #{reporterName} 
		 and ${dynamicSQL} 


			ORDER BY t.${sortC} ${order}
