mapper.select:
select

from doufu_today_work t

	1=1
	and t.PROJECT_GROUP_ID = #{projectGroupId} 
	and t.PROJECT_ID = #{projectId} 
	and t.PRODUCT_ID = #{productId} 
	and t.WORK_CONTENTS = #{workContents} 
	and t.WORK_DETAIL = #{workDetail} 
	and t.FINISH_PERCENT = #{finishPercent} 
	and t.DELAY_REASON = #{delayReason} 
	and t.ACCORD_YESTERDAY = #{accordYesterday} 
	and t.IS_IMPORTANT = #{isImportant} 
	and t.IS_EMERGENCY = #{isEmergency} 
	and t.IMPO_LEVEL = #{impoLevel} 
	and t.ID = #{id} 
	and t.DEL_FLAG = #{delFlag} 
	and t.STATUS = #{status} 
	and t.INST_ID = #{instId} 
	and t.LOGIN_IP = #{loginIp} 
	and t.LOGIN_DATE = #{loginDate} 
	and t.CREATE_BY = #{createBy} 
	and t.CREATE_DATE = #{createDate} 
	and t.UPDATE_BY = #{updateBy} 
	and t.UPDATE_DATE = #{updateDate} 
	and t.REMARKS = #{remarks} 
	 and ${dynamicSQL} 


	ORDER BY t.${sortC} ${order}

