mapper.delete:
delete from doufu_today_work

	1=1
	 and PROJECT_GROUP_ID = #{projectGroupId} 
	 and PROJECT_ID = #{projectId} 
	 and PRODUCT_ID = #{productId} 
	 and WORK_CONTENTS = #{workContents} 
	 and WORK_DETAIL = #{workDetail} 
	 and FINISH_PERCENT = #{finishPercent} 
	 and DELAY_REASON = #{delayReason} 
	 and ACCORD_YESTERDAY = #{accordYesterday} 
	 and IS_IMPORTANT = #{isImportant} 
	 and IS_EMERGENCY = #{isEmergency} 
	 and IMPO_LEVEL = #{impoLevel} 
	 and ID = #{id} 
	 and DEL_FLAG = #{delFlag} 
	 and STATUS = #{status} 
	 and INST_ID = #{instId} 
	 and LOGIN_IP = #{loginIp} 
	 and LOGIN_DATE = #{loginDate} 
	 and CREATE_BY = #{createBy} 
	 and CREATE_DATE = #{createDate} 
	 and UPDATE_BY = #{updateBy} 
	 and UPDATE_DATE = #{updateDate} 
	 and REMARKS = #{remarks}

update.foreach:
update doufu_today_work
product_Id = #{item.productId}, work_Contents=#{item.workContents}, work_Detail=#{item.workDetail}, finish_Percent=#{item.finishPercent}, delay_Reason=#{item.delayReason}, is_Important=#{item.isImportant}, is_Emergency=#{item.isEmergency}, update_By=#{item.updateBy}, update_Date=#{item.updateDate}
id = #{item.id}
